  44→/**
  45→ * Symbols used to identify hook types
  46→ */
  47→const GET_HOOK_SYMBOL = Symbol("fragno-get-hook");
  48→const MUTATOR_HOOK_SYMBOL = Symbol("fragno-mutator-hook");
  49→const STORE_SYMBOL = Symbol("fragno-store");
  50→
  51→/**
  52→ * Check if a value contains files that should be sent as FormData.
  53→ * @internal
  54→ */
  55→function containsFiles(value: unknown): boolean {
  56→  if (value instanceof File || value instanceof Blob) {
  57→    return true;
  58→  }
  59→
  60→  if (value instanceof FormData) {
  61→    return true;
  62→  }
  63→
  64→  if (typeof value === "object" && value !== null) {
  65→    return Object.values(value).some(
  66→      (v) => v instanceof File || v instanceof Blob || v instanceof FormData,
  67→    );
  68→  }
  69→
  70→  return false;
  71→}
  72→
  73→/**
  74→ * Convert an object containing files to FormData.
  75→ * Handles nested File/Blob values by appending them directly.
  76→ * Other values are JSON-stringified.
  77→ * @internal
  78→ */
  79→function toFormData(value: object): FormData {
  80→  const formData = new FormData();
  81→
  82→  for (const [key, val] of Object.entries(value)) {
  83→    if (val instanceof File) {
  84→      formData.append(key, val, val.name);
  85→    } else if (val instanceof Blob) {
  86→      formData.append(key, val);
  87→    } else if (val !== undefined && val !== null) {
  88→      // For non-file values, stringify if needed
  89→      formData.append(key, typeof val === "string" ? val : JSON.stringify(val));
  90→    }
  91→  }
  92→
  93→  return formData;
  94→}
  95→
  96→/**
  97→ * Prepare request body and headers for sending.
  98→ * Handles FormData (file uploads) vs JSON data.
  99→ * @internal
 100→ */
 101→function prepareRequestBody(
 102→  body: unknown,
 103→  contentType?: RouteContentType,
 104→): { body: BodyInit | undefined; headers?: HeadersInit } {
 105→  if (body === undefined) {
 106→    return { body: undefined };
 107→  }
 108→
 109→  if (contentType === "application/octet-stream") {
 110→    if (
 111→      body instanceof ReadableStream ||
 112→      body instanceof Blob ||
 113→      body instanceof File ||
 114→      body instanceof ArrayBuffer ||
 115→      body instanceof Uint8Array
 116→    ) {
 117→      return { body: body as BodyInit, headers: { "Content-Type": "application/octet-stream" } };
 118→    }
 119→
 120→    throw new Error(
 121→      "Octet-stream routes only accept Blob, File, ArrayBuffer, Uint8Array, or ReadableStream bodies.",
 122→    );
 123→  }
 124→
 125→  // If already FormData, send as-is (browser sets Content-Type with boundary)
 126→  if (body instanceof FormData) {
 127→    return { body };
 128→  }
 129→
 130→  // If body is directly a File or Blob, wrap it in FormData
 131→  if (body instanceof File) {
 132→    const formData = new FormData();
 133→    formData.append("file", body, body.name);
 134→    return { body: formData };
 135→  }
 136→
 137→  if (body instanceof Blob) {
 138→    const formData = new FormData();
 139→    formData.append("file", body);
 140→    return { body: formData };
 141→  }
 142→
 143→  // If object contains files, convert to FormData
 144→  if (typeof body === "object" && body !== null && containsFiles(body)) {
 145→    return { body: toFormData(body) };
 146→  }
 147→
 148→  // Otherwise, JSON-stringify
 149→  return {
 150→    body: JSON.stringify(body),
 151→    headers: { "Content-Type": "application/json" },
 152→  };
 153→}
 154→
 155→/**
 156→ * Merge request headers from multiple sources.
 157→ * Returns undefined if there are no headers to merge.
 158→ * @internal
 159→ */
 160→function mergeRequestHeaders(
 161→  ...headerSources: (HeadersInit | undefined)[]
 162→): Record<string, string> | undefined {
 163→  const result: Record<string, string> = {};
 164→  let hasHeaders = false;
 165→
 166→  for (const source of headerSources) {
 167→    if (!source) {
 168→      continue;
 169→    }
 170→
 171→    if (source instanceof Headers) {
 172→      for (const [key, value] of source.entries()) {
 173→        result[key] = value;
 174→        hasHeaders = true;
 175→      }
 176→    } else if (Array.isArray(source)) {
 177→      for (const [key, value] of source) {
 178→        result[key] = value;
 179→        hasHeaders = true;
 180→      }
 181→    } else {
 182→      for (const [key, value] of Object.entries(source)) {
 183→        result[key] = value;
 184→        hasHeaders = true;
 185→      }
 186→    }
 187→  }
 188→
 189→  return hasHeaders ? result : undefined;
 190→}
