 405→export type FragnoClientHookData<
 406→  TMethod extends HTTPMethod,
 407→  TPath extends string,
 408→  TOutputSchema extends StandardSchemaV1,
 409→  TErrorCode extends string,
 410→  TQueryParameters extends string,
 411→> = {
 412→  route: FragnoRouteConfig<
 413→    TMethod,
 414→    TPath,
 415→    StandardSchemaV1 | undefined,
 416→    TOutputSchema,
 417→    TErrorCode,
 418→    TQueryParameters
 419→  >;
 420→  query(args?: {
 421→    path?: MaybeExtractPathParamsOrWiden<TPath, string>;
 422→    query?: Record<TQueryParameters, string | undefined>;
 423→  }): Promise<StandardSchemaV1.InferOutput<TOutputSchema>>;
 424→  store(args?: {
 425→    path?: MaybeExtractPathParamsOrWiden<TPath, string | ReadableAtom<string>>;
 426→    query?: Record<TQueryParameters, string | undefined | ReadableAtom<string | undefined>>;
 427→  }): FetcherStore<StandardSchemaV1.InferOutput<TOutputSchema>, FragnoClientError<TErrorCode>>;
 428→  [GET_HOOK_SYMBOL]: true;
 429→} & {
 430→  // Phantom field that preserves the specific TOutputSchema type parameter
 431→  // in the structural type. This makes the type covariant, allowing more
 432→  // specific schema types (like z.ZodString) to be assigned to variables
 433→  // typed with more general schema types (like StandardSchemaV1<any, any>)
 434→  readonly _outputSchema?: TOutputSchema;
 435→};
