 475→/**
 476→ * @internal
 477→ */
 478→export function buildUrl<TPath extends string>(
 479→  config: {
 480→    baseUrl?: string;
 481→    mountRoute: string;
 482→    path: TPath;
 483→  },
 484→  params: {
 485→    pathParams?: Record<string, string | ReadableAtom<string>>;
 486→    queryParams?: Record<string, string | undefined | ReadableAtom<string | undefined>>;
 487→  },
 488→): string {
 489→  const { baseUrl = "", mountRoute, path } = config;
 490→  const { pathParams, queryParams } = params ?? {};
 491→
 492→  const normalizedPathParams = unwrapObject(pathParams) as ExtractPathParams<TPath, string>;
 493→  const normalizedQueryParams = unwrapObject(queryParams) ?? {};
 494→
 495→  // Filter out undefined values to prevent URLSearchParams from converting them to string "undefined"
 496→  const filteredQueryParams = Object.fromEntries(
 497→    Object.entries(normalizedQueryParams).filter(([_, value]) => value !== undefined),
 498→  ) as Record<string, string>;
 499→
 500→  const searchParams = new URLSearchParams(filteredQueryParams);
 501→  const builtPath = buildPath(path, normalizedPathParams ?? {});
 502→  const search = searchParams.toString() ? `?${searchParams.toString()}` : "";
 503→  return `${baseUrl}${mountRoute}${builtPath}${search}`;
 504→}
 505→
 506→/**
 507→ * This method returns an array, which can be passed directly to nanostores.
 508→ *
 509→ * The returned array is always: path, pathParams (In order they appear in the path), queryParams (In alphabetical order)
 510→ * Missing pathParams are replaced with "<missing>".
 511→ * Atoms with undefined values are wrapped in computed atoms that map undefined to "" to avoid nanoquery treating the key as incomplete.
 512→ * @param path
 513→ * @param params
 514→ * @returns
 515→ * @internal
 516→ */
 517→export function getCacheKey<TMethod extends HTTPMethod, TPath extends string>(
 518→  method: TMethod,
 519→  path: TPath,
 520→  params?: {
 521→    pathParams?: Record<string, string | ReadableAtom<string>>;
 522→    queryParams?: Record<string, string | undefined | ReadableAtom<string | undefined>>;
 523→  },
 524→): (string | ReadableAtom<string>)[] {
 525→  if (!params) {
 526→    return [method, path];
 527→  }
 528→
 529→  const { pathParams, queryParams } = params;
 530→
 531→  const pathParamNames = extractPathParams(path);
 532→  const pathParamValues = pathParamNames.map((name) => pathParams?.[name] ?? "<missing>");
 533→
 534→  const queryParamValues = queryParams
 535→    ? Object.keys(queryParams)
 536→        .sort()
 537→        .map((key) => {
 538→          const value = queryParams[key];
 539→          // If it's an atom, wrap it to convert undefined to ""
 540→          if (value && typeof value === "object" && "get" in value) {
 541→            return computed(value as ReadableAtom<string | undefined>, (v) => v ?? "");
 542→          }
 543→          // Plain string value (or undefined)
 544→          return value ?? "";
 545→        })
 546→    : [];
 547→
 548→  return [method, path, ...pathParamValues, ...queryParamValues];
 549→}
 550→
 551→function isStreamingResponse(response: Response): false | "ndjson" | "octet-stream" {
 552→  const contentType = parseContentType(response.headers.get("content-type"));
 553→
 554→  if (!contentType) {
 555→    // Always assume 'normal' JSON by default.
 556→    return false;
 557→  }
 558→
 559→  const isChunked = response.headers.get("transfer-encoding") === "chunked";
 560→
 561→  if (!isChunked) {
 562→    return false;
 563→  }
 564→
 565→  if (contentType.subtype === "octet-stream") {
 566→    // TODO(Wilco): This is not actually supported properly
 567→    return "octet-stream";
 568→  }
 569→
 570→  if (contentType.subtype === "x-ndjson") {
 571→    return "ndjson";
 572→  }
 573→
 574→  return false;
 575→}
