 577→// Type guard to check if a hook is a GET hook
 578→/**
 579→ * @internal
 580→ */
 581→export function isGetHook<
 582→  TPath extends string,
 583→  TOutputSchema extends StandardSchemaV1,
 584→  TErrorCode extends string,
 585→  TQueryParameters extends string,
 586→>(
 587→  hook: unknown,
 588→): hook is FragnoClientHookData<"GET", TPath, TOutputSchema, TErrorCode, TQueryParameters> {
 589→  return (
 590→    typeof hook === "object" &&
 591→    hook !== null &&
 592→    GET_HOOK_SYMBOL in hook &&
 593→    hook[GET_HOOK_SYMBOL] === true
 594→  );
 595→}
 596→
 597→// Type guard to check if a hook is a mutator
 598→/**
 599→ * @internal
 600→ */
 601→export function isMutatorHook<
 602→  TMethod extends NonGetHTTPMethod,
 603→  TPath extends string,
 604→  TInputSchema extends StandardSchemaV1 | undefined,
 605→  TOutputSchema extends StandardSchemaV1 | undefined,
 606→  TErrorCode extends string,
 607→  TQueryParameters extends string,
 608→>(
 609→  hook: unknown,
 610→): hook is FragnoClientMutatorData<
 611→  TMethod,
 612→  TPath,
 613→  TInputSchema,
 614→  TOutputSchema,
 615→  TErrorCode,
 616→  TQueryParameters
 617→> {
 618→  return (
 619→    typeof hook === "object" &&
 620→    hook !== null &&
 621→    MUTATOR_HOOK_SYMBOL in hook &&
 622→    hook[MUTATOR_HOOK_SYMBOL] === true
 623→  );
 624→}
