 626→/**
 627→ * @internal
 628→ */
 629→export function isStore<TStore extends Store>(obj: unknown): obj is FragnoStoreData<TStore> {
 630→  return (
 631→    typeof obj === "object" && obj !== null && STORE_SYMBOL in obj && obj[STORE_SYMBOL] === true
 632→  );
