 676→export class ClientBuilder<
 677→  TRoutes extends readonly FragnoRouteConfig<
 678→    HTTPMethod,
 679→    string,
 680→    StandardSchemaV1 | undefined,
 681→    StandardSchemaV1 | undefined,
 682→    string,
 683→    string
 684→  >[],
 685→  TFragmentConfig extends FragnoFragmentSharedConfig<TRoutes>,
 686→> {
 687→  #publicConfig: FragnoPublicClientConfig;
 688→  #fragmentConfig: TFragmentConfig;
 689→  #fetcherConfig?: FetcherConfig;
 690→
 691→  #cache = new Map<string, CacheLine>();
 692→
 693→  #createFetcherStore;
 694→  #createMutatorStore;
 695→  #invalidateKeys;
 696→
 697→  constructor(publicConfig: FragnoPublicClientConfig, fragmentConfig: TFragmentConfig) {
 698→    this.#publicConfig = publicConfig;
 699→    this.#fragmentConfig = fragmentConfig;
 700→    this.#fetcherConfig = publicConfig.fetcherConfig;
 701→
 702→    const [createFetcherStore, createMutatorStore, { invalidateKeys }] = nanoquery({
 703→      cache: this.#cache,
 704→    });
 705→    this.#createFetcherStore = createFetcherStore;
 706→    this.#createMutatorStore = createMutatorStore;
 707→    this.#invalidateKeys = invalidateKeys;
 708→  }
 709→
 710→  get cacheEntries(): Readonly<Record<string, CacheLine>> {
 711→    return Object.fromEntries(this.#cache.entries());
 712→  }
 713→
 714→  createStore<const T extends object>(obj: T): FragnoStoreData<T> {
 715→    return { obj: obj, [STORE_SYMBOL]: true };
 716→  }
 717→
 718→  /**
 719→   * Build a URL for a custom backend call using the configured baseUrl and mountRoute.
 720→   * Useful for fragment authors who need to make custom fetch calls.
 721→   */
 722→  buildUrl<TPath extends string>(
 723→    path: TPath,
 724→    params?: {
 725→      path?: MaybeExtractPathParamsOrWiden<TPath, string>;
 726→      query?: Record<string, string>;
 727→    },
 728→  ): string {
 729→    const baseUrl = this.#publicConfig.baseUrl ?? "";
 730→    const mountRoute = getMountRoute({
 731→      name: this.#fragmentConfig.name,
 732→      mountRoute: this.#publicConfig.mountRoute,
 733→    });
 734→
 735→    return buildUrl(
 736→      { baseUrl, mountRoute, path },
 737→      { pathParams: params?.path, queryParams: params?.query },
 738→    );
 739→  }
 740→
 741→  /**
 742→   * Get the configured fetcher function for custom backend calls.
 743→   * Returns fetch with merged options applied.
 744→   */
 745→  getFetcher(): {
 746→    fetcher: typeof fetch;
 747→    defaultOptions: RequestInit | undefined;
 748→  } {
 749→    return {
 750→      fetcher: this.#getFetcher(),
 751→      defaultOptions: this.#getFetcherOptions(),
 752→    };
 753→  }
 754→
 755→  #getFetcher(): typeof fetch {
 756→    if (this.#fetcherConfig?.type === "function") {
 757→      return this.#fetcherConfig.fetcher;
 758→    }
 759→    return fetch;
 760→  }
 761→
 762→  #getFetcherOptions(): RequestInit | undefined {
 763→    if (this.#fetcherConfig?.type === "options") {
 764→      return this.#fetcherConfig.options;
 765→    }
 766→    return undefined;
 767→  }
 768→
 769→  createHook<TPath extends ExtractGetRoutePaths<TFragmentConfig["routes"]>>(
 770→    path: ValidateGetRoutePath<TFragmentConfig["routes"], TPath>,
 771→    options?: CreateHookOptions,
 772→  ): FragnoClientHookData<
 773→    "GET",
 774→    TPath,
 775→    NonNullable<ExtractRouteByPath<TFragmentConfig["routes"], TPath, "GET">["outputSchema"]>,
 776→    NonNullable<ExtractRouteByPath<TFragmentConfig["routes"], TPath, "GET">["errorCodes"]>[number],
 777→    NonNullable<
 778→      ExtractRouteByPath<TFragmentConfig["routes"], TPath, "GET">["queryParameters"]
 779→    >[number]
 780→  > {
 781→    const route = this.#fragmentConfig.routes.find(
 782→      (
 783→        r,
 784→      ): r is FragnoRouteConfig<
 785→        "GET",
 786→        TPath,
 787→        StandardSchemaV1 | undefined,
 788→        StandardSchemaV1,
 789→        string,
 790→        string
 791→      > => r.path === path && r.method === "GET" && r.outputSchema !== undefined,
 792→    );
 793→
 794→    if (!route) {
 795→      throw new Error(`Route '${path}' not found or is not a GET route with an output schema.`);
 796→    }
 797→
 798→    return this.#createRouteQueryHook(route, options);
 799→  }
 800→
 801→  createMutator<TPath extends ExtractNonGetRoutePaths<TFragmentConfig["routes"]>>(
 802→    method: NonGetHTTPMethod,
 803→    path: TPath,
 804→    onInvalidate?: OnInvalidateFn<TPath>,
 805→  ): FragnoClientMutatorData<
 806→    NonGetHTTPMethod, // TODO: This can be any Method, but should be related to TPath
 807→    TPath,
 808→    ExtractRouteByPath<TFragmentConfig["routes"], TPath>["inputSchema"],
 809→    ExtractRouteByPath<TFragmentConfig["routes"], TPath>["outputSchema"],
 810→    NonNullable<ExtractRouteByPath<TFragmentConfig["routes"], TPath>["errorCodes"]>[number],
 811→    NonNullable<ExtractRouteByPath<TFragmentConfig["routes"], TPath>["queryParameters"]>[number]
 812→  > {
 813→    type TRoute = ExtractRouteByPath<TFragmentConfig["routes"], TPath>;
 814→
 815→    const route = this.#fragmentConfig.routes.find(
 816→      (
 817→        r,
 818→      ): r is FragnoRouteConfig<
 819→        NonGetHTTPMethod,
 820→        TPath,
 821→        TRoute["inputSchema"],
 822→        TRoute["outputSchema"],
 823→        string,
 824→        string
 825→      > => r.method !== "GET" && r.path === path && r.method === method,
 826→    );
 827→
 828→    if (!route) {
 829→      throw new Error(
 830→        `Route '${path}' not found or is a GET route with an input and output schema.`,
 831→      );
 832→    }
 833→
 834→    return this.#createRouteQueryMutator(route, onInvalidate);
 835→  }
 836→
 837→  #createRouteQueryHook<
 838→    TPath extends string,
 839→    TInputSchema extends StandardSchemaV1 | undefined,
 840→    TOutputSchema extends StandardSchemaV1,
 841→    TErrorCode extends string,
 842→    TQueryParameters extends string,
 843→  >(
 844→    route: FragnoRouteConfig<
 845→      "GET",
 846→      TPath,
 847→      TInputSchema,
 848→      TOutputSchema,
 849→      TErrorCode,
 850→      TQueryParameters
 851→    >,
 852→    options: CreateHookOptions = {},
 853→  ): FragnoClientHookData<"GET", TPath, TOutputSchema, TErrorCode, TQueryParameters> {
 854→    if (route.method !== "GET") {
 855→      throw new Error(
 856→        `Only GET routes are supported for hooks. Route '${route.path}' is a ${route.method} route.`,
 857→      );
 858→    }
 859→
 860→    if (!route.outputSchema) {
 861→      throw new Error(
 862→        `Output schema is required for GET routes. Route '${route.path}' has no output schema.`,
 863→      );
 864→    }
 865→
 866→    const baseUrl = this.#publicConfig.baseUrl ?? "";
 867→    const mountRoute = getMountRoute({
 868→      name: this.#fragmentConfig.name,
 869→      mountRoute: this.#publicConfig.mountRoute,
 870→    });
 871→    const fetcher = this.#getFetcher();
 872→    const fetcherOptions = this.#getFetcherOptions();
 873→
 874→    async function callServerSideHandler(params: {
 875→      pathParams?: Record<string, string | ReadableAtom<string>>;
 876→      queryParams?: Record<string, string | undefined | ReadableAtom<string | undefined>>;
 877→    }): Promise<Response> {
 878→      const { pathParams, queryParams } = params ?? {};
 879→
 880→      const normalizedPathParams = unwrapObject(pathParams) as ExtractPathParams<TPath, string>;
 881→      const normalizedQueryParams = unwrapObject(queryParams) ?? {};
 882→
 883→      // Filter out undefined values to prevent URLSearchParams from converting them to string "undefined"
 884→      const filteredQueryParams = Object.fromEntries(
 885→        Object.entries(normalizedQueryParams).filter(([_, value]) => value !== undefined),
 886→      ) as Record<string, string>;
 887→
 888→      const searchParams = new URLSearchParams(filteredQueryParams);
 889→
 890→      const result = await route.handler(
 891→        RequestInputContext.fromSSRContext({
 892→          method: route.method,
 893→          path: route.path,
 894→          pathParams: normalizedPathParams,
 895→          searchParams,
 896→        }),
 897→        new RequestOutputContext(route.outputSchema),
 898→      );
 899→
 900→      return result;
 901→    }
 902→
 903→    async function executeQuery(params?: {
 904→      pathParams?: Record<string, string | ReadableAtom<string>>;
 905→      queryParams?: Record<string, string | undefined | ReadableAtom<string | undefined>>;
 906→    }): Promise<Response> {
 907→      const { pathParams, queryParams } = params ?? {};
 908→
 909→      if (typeof window === "undefined") {
 910→        return task(async () => callServerSideHandler({ pathParams, queryParams }));
 911→      }
 912→
 913→      const url = buildUrl({ baseUrl, mountRoute, path: route.path }, { pathParams, queryParams });
 914→
 915→      let response: Response;
 916→      try {
 917→        response = fetcherOptions ? await fetcher(url, fetcherOptions) : await fetcher(url);
 918→      } catch (error) {
 919→        throw FragnoClientFetchError.fromUnknownFetchError(error);
 920→      }
 921→
 922→      if (!response.ok) {
 923→        throw await FragnoClientApiError.fromResponse<TErrorCode>(response);
 924→      }
 925→
 926→      return response;
 927→    }
 928→
 929→    return {
 930→      route,
 931→      store: (args) => {
 932→        const { path, query } = args ?? {};
 933→
 934→        const key = getCacheKey(route.method, route.path, {
 935→          pathParams: path,
 936→          queryParams: query,
 937→        });
 938→
 939→        const store = this.#createFetcherStore<
 940→          StandardSchemaV1.InferOutput<TOutputSchema>,
 941→          FragnoClientError<TErrorCode>
 942→        >(key, {
 943→          fetcher: async (): Promise<StandardSchemaV1.InferOutput<TOutputSchema>> => {
 944→            if (SSR_ENABLED) {
 945→              const initialData = getInitialData(
 946→                key.map((d) => (typeof d === "string" ? d : d.get())).join(""),
 947→              );
 948→
 949→              if (initialData) {
 950→                return initialData;
 951→              }
 952→            }
 953→
 954→            const response = await executeQuery({ pathParams: path, queryParams: query });
 955→            const isStreaming = isStreamingResponse(response);
 956→
 957→            if (!isStreaming) {
 958→              return response.json() as Promise<StandardSchemaV1.InferOutput<TOutputSchema>>;
 959→            }
 960→
 961→            if (typeof window === "undefined") {
 962→              return [];
 963→            }
 964→
 965→            if (isStreaming === "ndjson") {
 966→              const storeAdapter: NdjsonStreamingStore<TOutputSchema, TErrorCode> = {
 967→                setData: (value) => {
 968→                  store.set({
 969→                    ...store.get(),
 970→                    loading: !(Array.isArray(value) && value.length > 0),
 971→                    data: value as InferOr<TOutputSchema, undefined>,
 972→                  });
 973→                },
 974→                setError: (value) => {
 975→                  store.set({
 976→                    ...store.get(),
 977→                    error: value,
 978→                  });
 979→                },
 980→              };
 981→
 982→              // Start streaming in background and return first item
 983→              const { firstItem } = await handleNdjsonStreamingFirstItem(response, storeAdapter);
 984→              return [firstItem];
 985→            }
 986→
 987→            if (isStreaming === "octet-stream") {
 988→              // TODO(Wilco): Implement this
 989→              throw new Error("Octet-stream streaming is not supported.");
 990→            }
 991→
 992→            throw new Error("Unreachable");
 993→          },
 994→
 995→          onErrorRetry: options?.onErrorRetry,
 996→          dedupeTime: Infinity,
 997→        });
 998→
 999→        if (typeof window === "undefined") {
1000→          addStore(store);
1001→        }
1002→
1003→        return store;
1004→      },
1005→      query: async (args) => {
1006→        const { path, query } = args ?? {};
1007→
1008→        const response = await executeQuery({ pathParams: path, queryParams: query });
1009→
1010→        const isStreaming = isStreamingResponse(response);
1011→
1012→        if (!isStreaming) {
1013→          return (await response.json()) as StandardSchemaV1.InferOutput<TOutputSchema>;
1014→        }
1015→
1016→        if (isStreaming === "ndjson") {
1017→          const { streamingPromise } = await handleNdjsonStreamingFirstItem(response);
1018→          // Resolves once the stream is done
1019→          return await streamingPromise;
1020→        }
1021→
1022→        if (isStreaming === "octet-stream") {
1023→          // TODO(Wilco): Implement this
1024→          throw new Error("Octet-stream streaming is not supported.");
1025→        }
1026→
1027→        throw new Error("Unreachable");
1028→      },
1029→      [GET_HOOK_SYMBOL]: true,
1030→    };
1031→  }
1032→
1033→  #createRouteQueryMutator<
1034→    TPath extends string,
1035→    TInputSchema extends StandardSchemaV1 | undefined,
1036→    TOutputSchema extends StandardSchemaV1 | undefined,
1037→    TErrorCode extends string,
1038→    TQueryParameters extends string,
1039→  >(
1040→    route: FragnoRouteConfig<
1041→      NonGetHTTPMethod,
1042→      TPath,
1043→      TInputSchema,
1044→      TOutputSchema,
1045→      TErrorCode,
1046→      TQueryParameters
1047→    >,
1048→    onInvalidate: OnInvalidateFn<TPath> = (invalidate, params) =>
1049→      invalidate("GET", route.path, params),
1050→  ): FragnoClientMutatorData<
1051→    NonGetHTTPMethod,
1052→    TPath,
1053→    TInputSchema,
1054→    TOutputSchema,
1055→    TErrorCode,
1056→    TQueryParameters
1057→  > {
1058→    const method = route.method;
1059→
1060→    const baseUrl = this.#publicConfig.baseUrl ?? "";
1061→    const mountRoute = getMountRoute({
1062→      name: this.#fragmentConfig.name,
1063→      mountRoute: this.#publicConfig.mountRoute,
1064→    });
1065→    const fetcher = this.#getFetcher();
1066→    const fetcherOptions = this.#getFetcherOptions();
1067→
1068→    async function executeMutateQuery({
1069→      body,
1070→      path,
1071→      query,
1072→    }: {
1073→      body?: InferOr<TInputSchema, undefined>;
1074→      path?: ExtractPathParamsOrWiden<TPath, string>;
1075→      query?: Record<string, string>;
1076→    }): Promise<Response> {
1077→      if (typeof window === "undefined") {
1078→        return task(async () =>
1079→          route.handler(
1080→            RequestInputContext.fromSSRContext({
1081→              inputSchema: route.inputSchema,
1082→              method,
1083→              path: route.path,
1084→              pathParams: (path ?? {}) as ExtractPathParams<TPath, string>,
1085→              searchParams: new URLSearchParams(query),
1086→              body,
1087→            }),
1088→            new RequestOutputContext(route.outputSchema),
1089→          ),
1090→        );
1091→      }
1092→
1093→      const url = buildUrl(
1094→        { baseUrl, mountRoute, path: route.path },
1095→        { pathParams: path, queryParams: query },
1096→      );
1097→
1098→      let response: Response;
1099→      try {
1100→        const { body: preparedBody, headers: bodyHeaders } = prepareRequestBody(
1101→          body,
1102→          route.contentType,
1103→        );
1104→
1105→        // Merge headers: fetcherOptions headers + body-specific headers (e.g., Content-Type for JSON)
1106→        // For FormData, bodyHeaders is undefined and browser sets Content-Type with boundary automatically
1107→        const mergedHeaders = mergeRequestHeaders(
1108→          fetcherOptions?.headers as HeadersInit | undefined,
1109→          bodyHeaders,
1110→        );
1111→
1112→        const requestOptions: RequestInit & { duplex?: "half" } = {
1113→          ...fetcherOptions,
1114→          method,
1115→          body: preparedBody,
1116→          ...(mergedHeaders ? { headers: mergedHeaders } : {}),
1117→        };
1118→        if (preparedBody instanceof ReadableStream) {
1119→          requestOptions.duplex = "half";
1120→        }
1121→        response = await fetcher(url, requestOptions);
1122→      } catch (error) {
1123→        throw FragnoClientFetchError.fromUnknownFetchError(error);
1124→      }
1125→
1126→      if (!response.ok) {
1127→        throw await FragnoClientApiError.fromResponse<TErrorCode>(response);
1128→      }
1129→
1130→      return response;
1131→    }
1132→
1133→    const mutatorStore: FragnoClientMutatorData<
1134→      NonGetHTTPMethod,
1135→      TPath,
1136→      TInputSchema,
1137→      TOutputSchema,
1138→      TErrorCode,
1139→      TQueryParameters
1140→    >["mutatorStore"] = this.#createMutatorStore(
1141→      async ({ data }) => {
1142→        if (typeof window === "undefined") {
1143→          // TODO(Wilco): Handle server-side rendering.
1144→        }
1145→
1146→        const { body, path, query } = data as {
1147→          body?: InferOr<TInputSchema, undefined>;
1148→          path?: ExtractPathParamsOrWiden<TPath, string>;
1149→          query?: Record<string, string>;
1150→        };
1151→
1152→        if (typeof body === "undefined" && route.inputSchema !== undefined) {
1153→          throw new Error("Body is required.");
1154→        }
1155→
1156→        const response = await executeMutateQuery({ body, path, query });
1157→
1158→        onInvalidate(this.#invalidate.bind(this), {
1159→          pathParams: (path ?? {}) as MaybeExtractPathParamsOrWiden<TPath, string>,
1160→          queryParams: query,
1161→        });
1162→
1163→        if (response.status === 201 || response.status === 204) {
1164→          return undefined;
1165→        }
1166→
1167→        const isStreaming = isStreamingResponse(response);
1168→
1169→        if (!isStreaming) {
1170→          return response.json();
1171→        }
1172→
1173→        if (typeof window === "undefined") {
1174→          return [];
1175→        }
1176→
1177→        if (isStreaming === "ndjson") {
1178→          const storeAdapter: NdjsonStreamingStore<NonNullable<TOutputSchema>, TErrorCode> = {
1179→            setData: (value) => {
1180→              mutatorStore.set({
1181→                ...mutatorStore.get(),
1182→                loading: !(Array.isArray(value) && value.length > 0),
1183→                data: value as InferOr<TOutputSchema, undefined>,
1184→              });
1185→            },
1186→            setError: (value) => {
1187→              mutatorStore.set({
1188→                ...mutatorStore.get(),
1189→                error: value,
1190→              });
1191→            },
1192→          };
1193→
1194→          // Start streaming in background and return first item
1195→          const { firstItem } = await handleNdjsonStreamingFirstItem(response, storeAdapter);
1196→
1197→          // Return the first item immediately. The streaming will continue in the background
1198→          return [firstItem];
1199→        }
1200→
1201→        if (isStreaming === "octet-stream") {
1202→          // TODO(Wilco): Implement this
1203→          throw new Error("Octet-stream streaming is not supported.");
1204→        }
1205→
1206→        throw new Error("Unreachable");
1207→      },
1208→      {
1209→        onError: (error) => {
1210→          console.error("Error in mutatorStore", error);
1211→        },
1212→      },
1213→    );
1214→
1215→    const mutateQuery = (async (data) => {
1216→      // TypeScript infers the fields to not exist, even though they might
1217→      const { body, path, query } = data as {
1218→        body?: InferOr<TInputSchema, undefined>;
1219→        path?: ExtractPathParamsOrWiden<TPath, string>;
1220→        query?: Record<string, string>;
1221→      };
1222→
1223→      if (typeof body === "undefined" && route.inputSchema !== undefined) {
1224→        throw new Error("Body is required for mutateQuery");
1225→      }
1226→
1227→      const response = await executeMutateQuery({ body, path, query });
1228→
1229→      if (response.status === 201 || response.status === 204) {
1230→        return undefined;
1231→      }
1232→
1233→      const isStreaming = isStreamingResponse(response);
1234→
1235→      if (!isStreaming) {
1236→        return response.json();
1237→      }
1238→
1239→      if (isStreaming === "ndjson") {
1240→        const { streamingPromise } = await handleNdjsonStreamingFirstItem(response);
1241→        // Resolves once the stream is done, i.e. we block until done
1242→        return await streamingPromise;
1243→      }
1244→
1245→      if (isStreaming === "octet-stream") {
1246→        throw new Error("Octet-stream streaming is not supported for mutations");
1247→      }
1248→
1249→      throw new Error("Unreachable");
1250→    }) satisfies FragnoClientMutatorData<
1251→      NonGetHTTPMethod,
1252→      TPath,
1253→      TInputSchema,
1254→      TOutputSchema,
1255→      TErrorCode,
1256→      TQueryParameters
1257→    >["mutateQuery"];
1258→
1259→    return {
1260→      route,
1261→      mutateQuery,
1262→      mutatorStore,
1263→      [MUTATOR_HOOK_SYMBOL]: true,
1264→    };
1265→  }
1266→
1267→  #invalidate<TPath extends string>(
1268→    method: HTTPMethod,
1269→    path: TPath,
1270→    params: {
1271→      pathParams?: MaybeExtractPathParamsOrWiden<TPath, string>;
1272→      queryParams?: Record<string, string>;
1273→    },
1274→  ) {
1275→    const prefixArray = getCacheKey(method, path, {
1276→      pathParams: params?.pathParams,
1277→      queryParams: params?.queryParams,
1278→    });
1279→
1280→    const prefix = prefixArray.map((k) => (typeof k === "string" ? k : k.get())).join("");
1281→
1282→    this.#invalidateKeys((key) => key.startsWith(prefix));
1283→  }
1284→}
1285→
1286→/**
1287→ * Create a client builder for fragments using the new fragment definition API.
1288→ * This is the same as createClientBuilder but works with FragmentDefinition.
1289→ */
1290→export function createClientBuilder<
1291→  TConfig,
1292→  TOptions extends FragnoPublicConfig,
1293→  TDeps,
1294→  TBaseServices,
1295→  TServices,
1296→  TServiceDependencies,
1297→  TPrivateServices,
1298→  TServiceThisContext extends RequestThisContext,
1299→  THandlerThisContext extends RequestThisContext,
1300→  TRequestStorage,
1301→  const TRoutesOrFactories extends readonly AnyRouteOrFactory[],
1302→  TInternalRoutes extends readonly AnyRouteOrFactory[] = readonly [],
1303→>(
1304→  definition: FragmentDefinition<
1305→    TConfig,
1306→    TOptions,
1307→    TDeps,
1308→    TBaseServices,
1309→    TServices,
1310→    TServiceDependencies,
1311→    TPrivateServices,
1312→    TServiceThisContext,
1313→    THandlerThisContext,
1314→    TRequestStorage,
1315→    TInternalRoutes
1316→  >,
1317→  publicConfig: FragnoPublicClientConfig,
1318→  routesOrFactories: TRoutesOrFactories,
1319→  authorFetcherConfig?: FetcherConfig,
1320→): ClientBuilder<
1321→  FlattenRouteFactories<TRoutesOrFactories>,
1322→  FragnoFragmentSharedConfig<FlattenRouteFactories<TRoutesOrFactories>>
1323→> {
1324→  // For client-side, we resolve route factories with dummy context
1325→  // This will be removed by the bundle plugin anyway
1326→  const dummyContext = {
1327→    config: {} as TConfig,
1328→    deps: {} as TDeps,
1329→    services: {} as TBaseServices & TServices,
1330→    serviceDeps: {},
1331→  };
1332→
1333→  const routes = resolveRouteFactories(dummyContext, routesOrFactories);
1334→
1335→  const fragmentConfig: FragnoFragmentSharedConfig<FlattenRouteFactories<TRoutesOrFactories>> = {
1336→    name: definition.name,
1337→    routes,
1338→  };
1339→
1340→  const mountRoute = getMountRoute({
1341→    name: definition.name,
1342→    mountRoute: publicConfig.mountRoute,
1343→  });
1344→  const mergedFetcherConfig = mergeFetcherConfigs(authorFetcherConfig, publicConfig.fetcherConfig);
1345→  const fullPublicConfig = {
1346→    ...publicConfig,
1347→    mountRoute,
1348→    fetcherConfig: mergedFetcherConfig,
1349→  };
1350→
1351→  return new ClientBuilder(fullPublicConfig, fragmentConfig);
1352→}
1353→
1354→export * from "./client-error";
1355→export type { FetcherConfig, FragnoPublicClientConfig } from "../api/shared-types";
1356→export type { FragnoFragmentSharedConfig } from "../api/fragment-instantiator";
1357→

