1393→      const combined = new Uint8Array(totalLength);
1394→      let offset = 0;
1395→      for (const chunk of chunks) {
1396→        combined.set(chunk, offset);
1397→        offset += chunk.length;
1398→      }
1399→      capturedBodyText = new TextDecoder().decode(combined);
1400→
1401→      return new Response(null, { status: 204 });
